// Generate unique run ID
const runId = `run_${Date.now()}_${Math.random().toString(36).substring(2, 9)}`;
$input.first().json.runId = runId;
return [$input.first()];

// Extract and filter posts from the last 7 days with high engagement
const items = $input.all();
const filteredPosts = [];

const sevenDaysAgo = new Date();
sevenDaysAgo.setDate(sevenDaysAgo.getDate() - 7);

for (const item of items) {
  const data = item.json;

  if (!data.posts || !Array.isArray(data.posts)) continue;

  for (const post of data.posts) {
    const postDate = new Date(post.postedAt || post.publishedAt || post.date);

    if (
      postDate >= sevenDaysAgo &&
      (post.likes || post.numLikes || 0) >= 100 &&
      !post.isReshare &&
      !post.isRepost &&
      post.text &&
      post.text.trim().length > 50
    ) {
      filteredPosts.push({
        creator: data.publicIdentifier || data.username,
        creatorName: data.fullName || data.name,
        postText: post.text,
        postUrl: post.url || post.postUrl,
        postedDate: post.postedAt || post.publishedAt || post.date,
        likes: post.likes || post.numLikes || 0,
        comments: post.comments || post.numComments || 0,
        contentType: post.contentType || 'text',
        imageUrl: post.imageUrl || null
      });
    }
  }
}

return filteredPosts.map(post => ({ json: post }));

// Parse OpenAI response and calculate final score
const items = $input.all();
const scoredPosts = [];

for (const item of items) {
  try {
    const aiResponse = item.json.message?.content || item.json.text || '';

    let analysis;
    const jsonMatch = aiResponse.match(/\{[\s\S]*\}/);
    if (jsonMatch) {
      analysis = JSON.parse(jsonMatch[0]);
    } else {
      analysis = {
        relevance: 3,
        valueDepth: 3,
        toneGood: true,
        leadMagnetOpportunity: false,
        leadMagnetSuggestion: '',
        reasoning: 'Could not parse AI response'
      };
    }

    const finalScore = (analysis.relevance * 2) + (analysis.valueDepth * 3);

    const originalData = $('Filter Posts (7 days, 100+ likes)').item.json;

    scoredPosts.push({
      json: {
        ...originalData,
        relevanceScore: analysis.relevance,
        valueDepthScore: analysis.valueDepth,
        toneGood: analysis.toneGood,
        leadMagnetOpportunity: analysis.leadMagnetOpportunity,
        leadMagnetSuggestion: analysis.leadMagnetSuggestion || '',
        aiReasoning: analysis.reasoning,
        finalScore: finalScore
      }
    });
  } catch (error) {
    console.log('Error processing post:', error);
  }
}

scoredPosts.sort((a, b) => b.json.finalScore - a.json.finalScore);
return scoredPosts.slice(0, 15);

// Select the best 7 posts from the top 15
const items = $input.all();

const top7Posts = items.slice(0, 7).map((item, index) => {
  const generatedPost = item.json.message?.content || item.json.text || item.json.postText;
  const originalData = $('Calculate & Rank (Top 15)').item.json;

  return {
    json: {
      rank: index + 1,
      creator: originalData.creatorName || originalData.creator,
      originalPostText: originalData.postText,
      originalUrl: originalData.postUrl,
      postedDate: originalData.postedDate,
      likes: originalData.likes,
      comments: originalData.comments,
      relevanceScore: originalData.relevanceScore,
      valueDepthScore: originalData.valueDepthScore,
      finalScore: originalData.finalScore,
      generatedPost: generatedPost.trim(),
      leadMagnetSuggestion: originalData.leadMagnetSuggestion || 'None',
      timestamp: new Date().toISOString(),
      weekOf: new Date().toISOString().split('T')[0]
    }
  };
});

return top7Posts;

// Generate PDF content from the 7 posts
