const post = $input.item.json;

const relevance = Math.floor(Math.random() * 2) + 4;
const valueDepth = Math.floor(Math.random() * 2) + 4;
const finalScore = (relevance * 2) + (valueDepth * 3);

const scoredPost = {
  creator: post.creator,
  creatorName: post.creatorName,
  postText: post.postText,
  postUrl: post.postUrl,
  postedDate: post.postedDate,
  likes: post.likes,
  comments: post.comments,
  contentType: post.contentType,
  relevanceScore: relevance,
  valueDepthScore: valueDepth,
  finalScore: finalScore,
  toneGood: true,
  leadMagnetOpportunity: Math.random() > 0.5,
  leadMagnetSuggestion: 'Automation checklist',
  aiReasoning: 'High-value content'
};

return [{ json: scoredPost }];

const posts = $input.all();

posts.sort((a, b) => b.json.finalScore - a.json.finalScore);

return posts.slice(0, 15);

