const creator = $input.item.json.creatorUrl;
const username = creator.split('/in/')[1];

const mockPosts = [];
for (let i = 0; i < 3; i++) {
  const daysAgo = Math.floor(Math.random() * 7);
  const date = new Date();
  date.setDate(date.getDate() - daysAgo);

  mockPosts.push({
    creator: username,
    creatorName: username.replace(/-/g, ' '),
    postText: 'Sample post about AI automation and productivity.',
    postUrl: 'https://www.linkedin.com/posts/' + username + '-activity',
    postedDate: date.toISOString(),
    likes: Math.floor(Math.random() * 300) + 100,
    comments: Math.floor(Math.random() * 50) + 10,
    contentType: 'text'
  });
}

return mockPosts.map(post => ({ json: post }));

const posts = $input.all();
const sevenDaysAgo = new Date();
sevenDaysAgo.setDate(sevenDaysAgo.getDate() - 7);

const filtered = posts.filter(item => {
  const post = item.json;
  const postDate = new Date(post.postedDate);
  return postDate >= sevenDaysAgo && post.likes >= 100;
});

return filtered;

const post = $input.item.json;

const relevance = Math.floor(Math.random() * 2) + 4;
const valueDepth = Math.floor(Math.random() * 2) + 4;
const finalScore = (relevance * 2) + (valueDepth * 3);

const scoredPost = {
  creator: post.creator,
  creatorName: post.creatorName,
  postText: post.postText,
  postUrl: post.postUrl,
  postedDate: post.postedDate,
  likes: post.likes,
  comments: post.comments,
  contentType: post.contentType,
  relevanceScore: relevance,
  valueDepthScore: valueDepth,
  finalScore: finalScore,
  toneGood: true,
  leadMagnetOpportunity: Math.random() > 0.5,
  leadMagnetSuggestion: 'Automation checklist',
  aiReasoning: 'High-value content'
};

return [{ json: scoredPost }];

const posts = $input.all();

posts.sort((a, b) => b.json.finalScore - a.json.finalScore);

return posts.slice(0, 15);

const posts = $input.all();

const top7 = posts.slice(0, 7).map((item, index) => {
  return {
    json: {
      rank: index + 1,
      creator: item.json.creatorName,
      originalPost: item.json.postText,
      originalUrl: item.json.postUrl,
      likes: item.json.likes,
      comments: item.json.comments,
      relevanceScore: item.json.relevanceScore,
      valueDepthScore: item.json.valueDepthScore,
      finalScore: item.json.finalScore,
      generatedPost: item.json.generatedText,
      leadMagnet: item.json.leadMagnetSuggestion,
      weekOf: item.json.weekOf
    }
  };
});

return top7;
