// MOCK SCRAPING - Replace with Apify
// This simulates scraped LinkedIn posts

const creator = $input.item.json.creatorUrl;
const username = creator.split('/in/')[1];

// Simulate 3 posts per creator
const mockPosts = [];
for (let i = 0; i < 3; i++) {
  const daysAgo = Math.floor(Math.random() * 7);
  const date = new Date();
  date.setDate(date.getDate() - daysAgo);

  mockPosts.push({
    creator: username,
    creatorName: username.replace(/-/g, ' '),
    postText: `Sample post ${i + 1} about AI automation and productivity. This would be real LinkedIn content in production.`,
    postUrl: `https://www.linkedin.com/posts/${username}-activity-${Date.now()}`,
    postedDate: date.toISOString(),
    likes: Math.floor(Math.random() * 300) + 100,
    comments: Math.floor(Math.random() * 50) + 10,
    contentType: 'text'
  });
}

return mockPosts.map(post => ({ json: post }));

// Filter posts from last 7 days with 100+ likes
const posts = $input.all();
const sevenDaysAgo = new Date();
sevenDaysAgo.setDate(sevenDaysAgo.getDate() - 7);

const filtered = posts.filter(item => {
  const post = item.json;
  const postDate = new Date(post.postedDate);
  return postDate >= sevenDaysAgo && post.likes >= 100;
});

return filtered;

// MOCK AI SCORING - Replace with OpenAI
// This simulates GPT-4 analysis

const post = $input.item.json;

// Simulate AI scores
const relevance = Math.floor(Math.random() * 2) + 4; // 4-5
const valueDepth = Math.floor(Math.random() * 2) + 4; // 4-5
const finalScore = (relevance * 2) + (valueDepth * 3);

const scoredPost = {
  ...post,
  relevanceScore: relevance,
  valueDepthScore: valueDepth,
  finalScore: finalScore,
  toneGood: true,
  leadMagnetOpportunity: Math.random() > 0.5,
  leadMagnetSuggestion: Math.random() > 0.5 ? 'Automation checklist' : 'None',
  aiReasoning: 'High-value content about practical automation'
};

return [{ json: scoredPost }];

// Rank posts by final score and keep top 15
const posts = $input.all();

// Sort by finalScore descending
posts.sort((a, b) => b.json.finalScore - a.json.finalScore);

// Return top 15
return posts.slice(0, 15);

// MOCK POST GENERATION - Replace with OpenAI GPT-4
// This simulates generated LinkedIn posts

const post = $input.item.json;

const generatedPost = {
  ...post,
  generatedText: `AI-generated post based on: ${post.postText.substring(0, 50)}...\n\nThis would be a fully generated LinkedIn post in production, written in your brand voice.\n\nKey insights:\n- Point 1\n- Point 2\n- Point 3\n\nWhat do you think?`,
  timestamp: new Date().toISOString(),
  weekOf: new Date().toISOString().split('T')[0]
};

return [{ json: generatedPost }];

// Select top 7 posts for final output
const posts = $input.all();

const top7 = posts.slice(0, 7).map((item, index) => {
  return {
    json: {
      rank: index + 1,
      creator: item.json.creatorName,
      originalPost: item.json.postText,
      originalUrl: item.json.postUrl,
      likes: item.json.likes,
      comments: item.json.comments,
      relevanceScore: item.json.relevanceScore,
      valueDepthScore: item.json.valueDepthScore,
      finalScore: item.json.finalScore,
      generatedPost: item.json.generatedText,
      leadMagnet: item.json.leadMagnetSuggestion,
      weekOf: item.json.weekOf
    }
  };
});

return top7;

// Output summary
const posts = $input.all();

const summary = {
  totalPosts: posts.length,
  weekOf: posts[0]?.json.weekOf || new Date().toISOString().split('T')[0],
  posts: posts.map(p => ({
    rank: p.json.rank,
    creator: p.json.creator,
    score: p.json.finalScore,
    preview: p.json.generatedPost.substring(0, 100) + '...'
  }))
};

return [{ json: summary }];
